mapper.select:
select count(1)
from
 (
 select  DATE_FORMAT(operation_time,'%Y-%m')
 as time from sdb_tuibei_kuaidi_record
 where member_id=#{member_id}
  )a
where a.time =#{month}

select count(1)
  from
   (
   select  DATE_FORMAT(operation_time,'%Y-%m-%d')
   as time from sdb_tuibei_kuaidi_record
   where member_id=#{member_id}
    )a
  where a.time =#{day}

    select count(1)
    from (

        select id ,member_id,
        trace_num as traceNum,
        operation_type,
        remark,ship_code
        from
        sdb_tuibei_kuaidi_record
        where 1=1

            and member_id=#{member_id}


            and trace_num=#{trace_num}



            and operation_type=#{operation_type}

) a

select id ,member_id,trace_num as traceNum,
date_format(operation_time,'%Y-%m-%d %H:%i:%s') as operation_time,
operation_type,
remark,ship_code,operator
from
sdb_tuibei_kuaidi_record
where 1=1

        and member_id=#{member_id}


        and trace_num=#{traceNum}



        and operation_type=#{operation_type}

    order by operation_time desc
  limit #{startIndex},#{pageSize}

select id ,member_id,trace_num as traceNum,operation_time,operation_type, remark,ship_code from sdb_tuibei_kuaidi_record where 1=1
and member_id=#{member_id}
and trace_num=#{traceNum}
limit 1

mapper.update:
update
sdb_tuibei_kuaidi_record



        operation_type=  #{operation_type},


        remark= #{remark},


where member_id=#{member_id}
and trace_num=#{traceNum}
